import React from 'react';
import people from './Data/people';
import './App.css'
function App() {
  return (
    <div >
      <h1>Uma dada pessoa:</h1>
      {
        people.map(
          (p) => (
            <div className='card'>
              <h2>{p.name}</h2>
              <p>idade:{p.idade}</p>
              <p>peso:{p.peso}</p>
              <p>altura:{p.altura}</p>
              <p>IMC: {Math.ceil(p.peso/(p.altura*p.altura))} -- {((p.peso/Math.pow(p.altura, 2))>30) &&('==> ⚠️ acima do peso')}</p>
            </div>
          )
        )
      }
    </div>

  );
}

export default App;
