import React, { useState } from 'react';

import '../CarForm/CarForm.css'

function CarForm() {
  const [name, setName] = useState('');
  const [brand, setBrand] = useState('');
  const [year, setYear] = useState('');
  const [color, setColor] = useState('');

  const handleSubmit = (event) =>{
    event.preventDefault()
    const newCar = {name, brand, year, color};
    //onAddCar(newCar);
    setName('')
    setBrand('')
    setYear('');
    setColor('');

    
  }
  return (
    <div className="content text-center">
      <h1>Adicionar Carro</h1>
      <form onSubmit={handleSubmit} className="form-container">
        <div className="form-group">
          <label className="col-form-label">Nome</label>
          <input className="form-control " value={name} onChange={(e) => setName(e.target.value)} />
        </div>
        <div className="form-group">
          <label className="col-form-label">Marca</label>
          <input className="form-control"  value={brand} onChange={(e) => setBrand(e.target.value)} />
        </div>
         <div className="form-group">
          <label className="col-form-label" >Ano</label>
          <input className="form-control" value={year}  onChange={(e) => setYear(e.target.value)} />
        </div>
        <div className="form-group">
          <label className="col-form-label">Cor</label>
          <input className="form-control"  value={color} onChange={(e) => setColor(e.target.value)} />
        </div>
       
        <div className="form-group">
          <span>
            <button type="button" className="mt-3 btn btn-light">Adicionar</button>

          </span>
          <span>
            <button type="submit" className="mt-3 btn btn-error">Cancelar</button>
          </span>


        </div>
      </form>
    </div>
  );
}

export default CarForm;
