import React, { useState } from 'react';
import CarForm from '../CarForm/CarForm.jsx';
import CarList from '../CarList/CarList.jsx';

function CarDetail() {
  const [cars, setCars] = useState([]);


  const handleAddCar = (car) => {
    setCars((prevCars) => [...prevCars, car]); // Adiciona novos carros ao array
  };
  

  return (
    <div>
    <h1>Cadastro de Carros</h1>
    <CarForm onAddCar={handleAddCar} />
    <CarList cars={cars} />
  </div>
  );
}

export default CarDetail;
