import ModalCarAdd from "../CarAdd/ModalCarAdd";
import './CarList.css'
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTrashCan, faPen } from "@fortawesome/free-solid-svg-icons";

import { useEffect, useState } from "react";

const CarList = () => {

  const [data, setData] = useState([])
  const [dataEdit, setDataEdit] = useState([])
  /*set do banco de dados (localstorage)*/
  useEffect(() => {
    const db_car = localStorage.getItem('cad_car')
    ? JSON.parse(localStorage.getItem('cad_car'))
    : []
    setData(db_car);
  },
  [setData]);

  const handleRemove = (name) => {
    const newArray = data.filter((item) => item.name !== name);
    setData(newArray)
    localStorage.setItem('cad_car', JSON.stringify(newArray))
  }
  return (
    <div className="content text-center">
      <div className="modal-button-container">
        <ModalCarAdd className="btn-add" onClick={()=> [setDataEdit({})]} />
      </div>

      <h1>Lista de Carros</h1>

      <div className="table-container">
        <table className="table">
          <thead>
            <tr>
              <th scope="col">Nome</th>
              <th scope="col">Marca</th>
              <th scope="col">Ano</th>
              <th scope="col">Cor</th>
              <th scope="col"></th>
            </tr>
          </thead>
          <tbody >
          {data.map(({name, brand, color, manufature}, index) => (
            <tr key={index}>
              <td>{name}</td>
              <td>{brand}</td>
              <td>{color}</td>
              <td>{manufature}</td>
              <td>
              <FontAwesomeIcon icon={faTrashCan} onClick={() => handleRemove(name)}              
              />
              </td>
              <td onClick={() => [setDataEdit({name, brand, color, manufature, index})]}>
              <FontAwesomeIcon icon={faPen}             
              />
              </td>


            </tr>


          )


          )}

          </tbody>
        </table>
      </div>
    </div>
  );
}

export default CarList;
