import './CarAdd.css';
import { useState, useEffect } from 'react';

const ModalCarAdd = ({ isOpen, onClose, data, setData, dataEdit }) => {
  const [name, setName] = useState('');
  const [brand, setBrand] = useState('');
  const [color, setColor] = useState('');
  const [manufacture, setManufacture] = useState('');

  useEffect(() => {
    if (dataEdit.id) {
      setName(dataEdit.name || '');
      setBrand(dataEdit.brand || '');
      setColor(dataEdit.color || '');
      setManufacture(dataEdit.manufacture || '');
    } else {
      setName('');
      setBrand('');
      setColor('');
      setManufacture('');
    }
  }, [dataEdit]);

  const handleSave = () => {
    if (!name || !brand || !color || !manufacture) return;

    let updatedData;
    if (dataEdit.id) {
      updatedData = data.map(item => 
        item.id === dataEdit.id ? { ...item, name, brand, color, manufacture } : item
      );
    } else {
      const newCar = {
        id: new Date().getTime(),
        name,
        brand,
        color,
        manufacture
      };
      updatedData = [...data, newCar];
    }

    localStorage.setItem('cad_car', JSON.stringify(updatedData));
    setData(updatedData);
    onClose();
  };

  if (!isOpen) return null;

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <div className="modal-header">
          <h5>{dataEdit.id ? 'Editar Carro' : 'Adicionar Carro'}</h5>
          <button onClick={onClose} className="btn-close">Ã—</button>
        </div>
        <div className="modal-body">
          <input type="text" placeholder="Nome" value={name} onChange={(e) => setName(e.target.value)} />
          <input type="text" placeholder="Marca" value={brand} onChange={(e) => setBrand(e.target.value)} />
          <input type="text" placeholder="Cor" value={color} onChange={(e) => setColor(e.target.value)} />
          <input type="text" placeholder="Ano" value={manufacture} onChange={(e) => setManufacture(e.target.value)} />
        </div>
        <div className="modal-footer">
          <button onClick={onClose} className="btn btn-secondary">Cancelar</button>
          <button onClick={handleSave} className="btn btn-primary">Salvar</button>
        </div>
      </div>
    </div>
  );
};

export default ModalCarAdd;
