import React, { useState } from 'react';
import '../CarForm/CarForm.css';

function CarForm({ onAddCar }) {
  const [carData, setCarData] = useState({ name: '', brand: '', color: '', year: '' });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setCarData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = () => {
    onAddCar(carData);
    setCarData({ name: '', brand: '', color: '', year: '' });
  };

  return (
    <div className="content text-center">
      <h1>Adicionar Carro</h1>
      <div className="form-container">
        {['Nome', 'Marca', 'Cor', 'Ano'].map((field, index) => (
          <div className="form-group" key={index}>
            <label className="col-form-label">{field}</label>
            <input
              className="form-control"
              name={field.toLowerCase()}
              value={carData[field.toLowerCase()]}
              onChange={handleChange}
            />
          </div>
        ))}
        <div className="form-group">
          <span>
            <button type="button" className="mt-3 btn btn-light" onClick={handleSubmit}>
              Adicionar
            </button>
          </span>
          <span>
            <button type="button" className="mt-3 btn btn-error">
              Cancelar
            </button>
          </span>
        </div>
      </div>
    </div>
  );
}

export default CarForm;
