import './CarAdd.css'
import { useState } from 'react';
function ModalCarAdd() {
    const ModalComp = ({data, setData, dataEdit}) =>{
        const [name, setName] = useState (dataEdit.name || '')
        const [brand, setBrand] = useState(dataEdit.brand || '')
        const [color, setColor] = useState(dataEdit.color || '')
        const [manufacture, setManufacture] = useState(dataEdit.manufacture || '')

        const handleSave = () => {
            /*Verifica se o campo foi preenchido */
            if(!name || !brand || !color || !manufacture) return;
            /*Verifica se é uma edição*/
            if (Object.keys(dataEdit).length){
                data[dataEdit.index] = {name, brand, color, manufacture}
            }

        };
        //construçaõ de novo array, caso não seja uma edição 
        const newDataArray = !Object.keys(dataEdit).length
        ?[...(data ? data : []), {name, brand, color, manufacture}]
        :[...(data ? data : [])];
        //set para guardar os dados no localstorage
        localStorage.setItem('cad_car', JSON.stringify(newDataArray))

        setData(newDataArray)


    return (
        <div>



            <button type="button" className="btn" data-bs-toggle="modal" data-bs-target="#exampleModal">
                Adicionar Carros
            </button>

            <div className="modal fade" id="exampleModal" tabIndex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
                <div className="modal-dialog">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h5 className="modal-title" id="exampleModalLabel">Adicionar Carros</h5>
                            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                        </div>
                        <div className="modal-body">
                            <div className="row">
                                <div className="col-6">
                                    <input type="text" className="form-control" placeholder="Name" onChange={(e)=> setName(e.target.value)}></input>
                                </div>
                                <div className="col-6">
                                    <input type="text" className="form-control" placeholder="Marca" onChange={(e)=> setBrand(e.target.value)}></input>
                                </div>

                            </div>
                            <div className="row mt-4">
                                <div className="col-6">
                                    <input type="text" className="form-control" placeholder="Cor" onChange={(e)=> setColor(e.target.value)}></input>
                                </div>
                                <div className="col-6">
                                    <input type="text" className="form-control" placeholder="Ano" onChange={(e)=> setManufacture(e.target.value)}></input>
                                </div>

                            </div>
                        </div>
                        <div className="modal-footer">
                            <button type="button" className="btn btn-secondary btn-danger" data-bs-dismiss="modal">Cancelar</button>
                            <button type="button" className="btn btn-primary btn-success" onClick={handleSave}>Salvar</button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )

}

}



export default ModalCarAdd;
